import { createSlice } from '@reduxjs/toolkit';

const initialState = {
  user: JSON.parse(localStorage.getItem('user')) || null,
};

const authSlice = createSlice({
  name: 'auth',
  initialState,
  reducers: {
    setUser(state, action) {
      console.log("‚úÖ SET_USER reducer called:", action.payload);
      state.user = action.payload;
      try {
        localStorage.setItem('user', JSON.stringify(action.payload));
        console.log("‚úÖ Saved user to localStorage:", localStorage.getItem('user'));
      } catch (err) {
        console.error("‚ùå Failed to save to localStorage:", err);
      }
    },
    logout(state) {
      state.user = null;
      localStorage.removeItem('user');
      console.log("üö™ Logged out and removed from localStorage");
    },
  },
});

export const { setUser, logout } = authSlice.actions;
export default authSlice.reducer;
