// TabBar.jsx
import React from "react";
import { Link } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";
import { logout } from "../authReducer";
import "../Styles/TabBar.css";

function TabBar({ toggleModal }) {
  const user = useSelector((state) => state.auth.user);
  const dispatch = useDispatch();

  return (
    <div className="tab-bar">
      <div className="tab-item">
        <Link to="/home" onClick={(e) => toggleModal('/home')}>
          <i className="fas fa-home"></i>
          <span>Home</span>
        </Link>
      </div>
      {user && (
        <>
          <div className="tab-item">
            <Link to="/search" onClick={(e) => toggleModal('/search')}>
              <i className="fas fa-edit"></i>
              <span>Edit Posts</span>
            </Link>
          </div>
          <div className="tab-item">
            <Link to="/add-post" onClick={(e) => toggleModal('/add-post')}>
              <i className="fas fa-square-plus"></i>
              <span>Add Post</span>
            </Link>
          </div>
          <div className="tab-item">
            <Link to="/chats" onClick={(e) => toggleModal('/chats')}>
              <i className="fas fa-ban"></i>
              <span>Manage Users</span>
            </Link>
          </div>
        </>
      )}
      <div className="tab-item" onClick={() => { dispatch(logout()); toggleModal('/login'); }}>
        <Link to="/login">
          <i className="fas fa-user"></i>
          <span>Profile</span>
        </Link>
      </div>
    </div>
  );
}

export default TabBar;
