// Home.jsx
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom'; 
import PostCard from '../Components/PostCard';
import TabBar from '../Components/TabBar';
import { AuthContext } from '../App';
import "../Styles/Home.css";
import Modal from '../Modal/Modal';

export default function Home() {
  const [posts, setPosts] = useState([]);
  const [users, setUsers] = useState([]);
  const [likes, setLikes] = useState([]);
  const { user } = React.useContext(AuthContext);

  const [modal, setModal] = useState(false);  
  const [nextLocation, setNextLocation] = useState(null);  
  const navigate = useNavigate(); 

  const toggleModal = (location) => {
    setNextLocation(location); 
    setModal(true); 
  };

  const confirmNavigation = () => {
    if (nextLocation) {
      navigate(nextLocation); 
    }
    setModal(false); 
  };

  const cancelNavigation = () => {
    setModal(false); 
  };

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const postsResponse = await fetch("http://localhost:3001/posts");
        const postsData = await postsResponse.json();
        setPosts(postsData);
      } catch (error) {
        console.error("Error fetching posts:", error);
      }
    };

    const fetchUsers = async () => {
      try {
        const usersResponse = await fetch("http://localhost:3001/users");
        const usersData = await usersResponse.json();
        setUsers(usersData);
      } catch (error) {
        console.error("Error fetching users:", error);
      }
    };

    const fetchLikes = async () => {
      try {
        const likesResponse = await fetch("http://localhost:3001/likes");
        const likesData = await likesResponse.json();
        setLikes(likesData);
      } catch (error) {
        console.error("Error fetching likes:", error);
      }
    };

    fetchPosts();
    fetchUsers();
    fetchLikes();
  }, []);

  return (
    <div className="background-container">
      <TabBar toggleModal={toggleModal} />  

      {modal && (
        <Modal 
          onConfirm={confirmNavigation} 
          onCancel={cancelNavigation}
        />
      )}

      <div className="posts-container">
        {posts.map(post => {
          const matchedUser = users.find(user => user.id === post.userId);
          const likeCount = likes.filter(like => like.post_id === post.id).length;

          return (
            <div key={post.id}>
              <PostCard
                post={post}
                author={matchedUser ? matchedUser.username : "Unknown"}
                date={post.created_at}
                onDelete={() => console.log("Delete post", post.id)}
                onEdit={() => console.log("Edit post", post.id)}
                onLike={() => handleLikePost(post.id)} 
              />
              <div className="like-container">
                <button onClick={() => handleLikePost(post.id)}>
                  {likes.some(like => like.userId === user?.id && like.post_id === post.id) ? 
                    <i className="fa-solid fa-heart"></i> :  
                    <i className="fa-regular fa-heart"></i>}
                </button>
                <span>{likeCount} Likes</span>
              </div>
              {user && user.role === "moderator" && (
                <button onClick={() => console.log("Delete post", post.id)}>Delete</button>
              )}
            </div>
          );
        })}
      </div>
    </div>
  );
}
