// src/Components/PostCard.js
import React from "react";
import { FaRegHeart, FaRegComment, FaShareAlt } from "react-icons/fa"; // Импортируем иконки из react-icons
import "../Styles/PostCard.css"; // Подключаем CSS

const PostCard = ({ post }) => {
  return (
    <div className="post-card">
      <div className="post-header">
        <img
          src={post.avatar || "https://abrakadabra.fun/uploads/posts/2021-12/1640528661_1-abrakadabra-fun-p-serii-chelovek-na-avu-1.png"} // Заменяем на URL аватара
          alt="avatar"
        />
        <div>
          <div className="post-author">{post.authorName || "Автор неизвестен"}</div>
          <div className="post-date">{post.date}</div>
        </div>
      </div>
      <div className="post-content">{post.content}</div>
      <div className="post-actions">
        <FaRegHeart className="like-icon" />
        <FaRegComment className="comment-icon" />
        <FaShareAlt className="share-icon" />
      </div>
    </div>
  );
};

export default PostCard;
