import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useSelector } from 'react-redux'; // Импортируем useSelector
import { toast } from 'react-toastify'; // Импортируем toast
import 'react-toastify/dist/ReactToastify.css'; // Импортируем стили для toast
import TabBar from '../Components/TabBar';
import '../Styles/AddPost.css'; 

function AddPost() {
    const user = useSelector((state) => state.auth.user); // Получаем пользователя из Redux
    const navigate = useNavigate();
    const [content, setContent] = useState('');
    const [image, setImage] = useState(null); // Состояние для изображения

    const handleAddPost = async () => {
        if (!content && !image) {
            toast.warn('Please fill in content or upload an image'); // Используем toast для предупреждения
            return;
        }

        const formData = new FormData();
        formData.append('userId', user.id);
        formData.append('content', content);
        if (image) {
            formData.append('image', image); // Добавляем изображение в FormData
        }

        try {
            const response = await fetch('http://localhost:3001/posts', {
                method: 'POST',
                body: formData, // Отправляем FormData
            });

            if (response.ok) {
                toast.success('Post added successfully!'); // Успешное уведомление
                navigate('/home'); 
            } else {
                toast.error('Failed to add post'); // Ошибка
            }
        } catch (error) {
            console.error('Error adding post:', error);
            toast.error('An error occurred while adding the post'); // Ошибка
        }
    };

    const handleImageChange = (e) => {
        setImage(e.target.files[0]); // Устанавливаем выбранное изображение
    };

    return (
        <div className="add-post-container">
            <TabBar />
            <div className="add-post-content">
                <div className="add-post-header">
                    <h1>Add Post</h1>
                </div>
                <div className="add-post-textarea">
                    <textarea
                        placeholder="Content"
                        value={content}
                        onChange={(e) => setContent(e.target.value)}
                    />
                </div>
                <div className="add-post-image">
                    <input
                        type="file"
                        accept="image/*"
                        onChange={handleImageChange} // Обработчик для выбора изображения
                    />
                </div>
                <div className="add-post-button">
                    <button onClick={handleAddPost}>Add Post</button>
                </div>
            </div>
        </div>
    );
}

export default AddPost;
