import React from "react";
import { useState } from "react";
import "../Styles/Modal.css";

export default function Modal() {
    const [modal, setModal] = useState(false);

    const toggleModal = () => {
        setModal(!modal);
    };


    return (
      <>
        <button onClick={toggleModal} className="btn-modal">
          Open Modal
        </button>

        {modal && (
          <div className="modal">
            <div className="overlay" onClick={toggleModal}></div>
            <div className="modal-content">
              <div className="modal-icon">
                <img src="/assets/user-icon.svg" alt="User Icon" />
              </div>
              <h2 className="modal-title">Title</h2>
              <p className="modal-description">Description text about this alert</p>
              <div className="modal-actions">
                <button className="modal-action-btn">Action</button>
                <button className="modal-cancel-btn" onClick={toggleModal}>Cancel</button>
              </div>
            </div>
          </div>
        )}
      </>
    );
}
