import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useSelector } from 'react-redux';
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import TabBar from '../Components/TabBar';
import '../Styles/AddPost.css';

function AddPost() {
  const user = useSelector((state) => state.auth.user);
  const navigate = useNavigate();
  const [content, setContent] = useState('');
  const [title, setTitle] = useState(''); // Added title field to match JSON structure
  const [media, setMedia] = useState(null);

  const handleAddPost = async () => {
    if (!content || !title) {
      toast.warn('Please fill in all fields');
      return;
    }

    if (!user || !user.id) {
      toast.warn('You must be logged in to create a post');
      navigate('/login');
      return;
    }

    // Check if user is banned
    if (user.role === 'ban') {
      toast.error('You are banned and cannot create posts');
      return;
    }

    const postData = {
      title,
      content,
      userId: user.id,
      created_at: new Date().toISOString(),
      image: null, // json-server doesn't support file uploads; set to null as per JSON
    };

    try {
      const response = await fetch('http://localhost:3001/posts', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(postData),
      });

      if (!response.ok) throw new Error('Failed to add post');
      toast.success('Post added successfully!');
      navigate('/home');
    } catch (error) {
      console.error('Error adding post:', error);
      toast.error('An error occurred while adding the post');
    }
  };

  return (
    <div className="add-post-container">
      <TabBar />
      <div className="add-post-content">
        <div className="add-post-header">
          <h1>Add Post</h1>
        </div>
        <div className="add-post-textarea">
          <input
            type="text"
            placeholder="Title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            required
          />
          <textarea
            placeholder="Content"
            value={content}
            onChange={(e) => setContent(e.target.value)}
            required
          />
        </div>
        <div className="add-post-file">
          <input
            type="file"
            onChange={(e) => setMedia(e.target.files[0])}
            disabled // Disabled since json-server doesn't support file uploads
          />
          <p className="file-upload-note">
            File uploads are not supported in this version.
          </p>
        </div>
        <div className="add-post-button">
          <button onClick={handleAddPost}>Add Post</button>
        </div>
      </div>
    </div>
  );
}

export default AddPost;
