import React from 'react';

export default function PostCard({ post, author, date, onDelete, onEdit, onLike, likes, user }) {
  const isLiked = likes.some(like => like.userId === user?.id && like.post_id === post.id);

  return (
    <div className="post-card">
      <div className="post-header">
        <img
          src="data:image/png;base64,"
          alt="avatar"
          className="avatar"
        />
        <div className="post-user-info">
          <span className="author">{author}</span>
          <span className="date">{date}</span>
        </div>
      </div>
      {post.title && <h3 className="post-title">{post.title}</h3>}
      <p>{post.content}</p>
      {post.image && (
        <img
          src={post.image}
          alt="Post media"
          className="post-image post-card-image"
          onError={(e) => {
            e.target.style.display = 'none'; // Hide broken images
            console.error('Failed to load image:', post.image);
          }}
        />
      )}
      <div className="post-actions">
        <div className="like-container">
          <button
            type="button"
            onClick={onLike}
            className={`like-button ${isLiked ? 'liked' : ''}`} // Add 'liked' class dynamically
          >
            {isLiked ? (
              <i className="fas fa-heart"></i>
            ) : (
              <i className="far fa-heart"></i>
            )}
          </button>
          <span>{likes.filter(like => like.post_id === post.id).length} Likes</span>
        </div>
        {user && (user.role === "admin" || user.role === "moderator") && (
          <button
            onClick={onDelete}
            className="delete-button"
          >
            <i className="fas fa-trash-alt"></i>
          </button>
        )}
      </div>
    </div>
  );
}
