import styles from "./Sidebar.module.css"; // ⬅️ без ../../
import { useState } from "react";
import ModalPost from "../Modal/ModalPost";

export default function NewPost() {
  const [showModal, setShowModal] = useState(false);

  return (
    <>
      <div className={styles.newPost}>
        <button className={styles.postButton} onClick={() => setShowModal(true)}>
          New Post
        </button>
      </div>
      {showModal && <ModalPost onClose={() => setShowModal(false)} />}
    </>
  );
}
