import React from 'react';
import { BrowserRouter, Routes, Route, Navigate } from "react-router-dom";
import Home from "./Pages/Home";
import Search from "./Pages/Search";
import AddPost from "./Pages/AddPost";
import Chats from "./Pages/Chats";
import Profile from "./Pages/Profile";
import Login from "./Pages/Login";
import SignUp from "./Pages/SignUp";
import { ToastContainer } from 'react-toastify';

export const AuthContext = React.createContext();  // Именованный экспорт

function App() {
  const [user, setUser] = React.useState(null);

  // Извлекаем данные из localStorage при монтировании компонента
  React.useEffect(() => {
  const storedUser = JSON.parse(localStorage.getItem('user'));
  if (storedUser) {
    dispatch(login(storedUser)); // Синхронизируем Redux с localStorage
  }
}, [dispatch]);

  function login(userData) {
    setUser(userData);
    localStorage.setItem('user', JSON.stringify(userData));  // Сохраняем данные в localStorage
  }

  function logout() {
    setUser(null);
    localStorage.removeItem('user');  // Удаляем данные из localStorage
  }

  return (
    <>
      <ToastContainer />
      <AuthContext.Provider value={{ user, login, logout }}>
        <BrowserRouter>
          <Routes>
            <Route
              path="/home" 
              element={user ? (user.role === null ? <Navigate to="/profile" /> : <Home />) : <Navigate to="/login" />}
            />
            <Route
              path="/search"
              element={user ? (user.role === null || user.role !== "moderator" ? <Navigate to="/profile" /> : <Search />) : <Navigate to="/login" />}
            />
            <Route
              path="/add-post"
              element={user ? (user.role === null ? <Navigate to="/profile" /> : <AddPost />) : <Navigate to="/login" />}
            />
            <Route
              path="/chats"
              element={user ? (user.role === null || user.role !== "admin" ? <Navigate to="/profile" /> : <Chats />) : <Navigate to="/login" />}
            />
            <Route
              path="/profile"
              element={<Profile />}
            />
            <Route
              path="/login"
              element={<Login />}
            />
            <Route
              path="/signup"
              element={<SignUp />}
            />
          </Routes>
        </BrowserRouter>
      </AuthContext.Provider>
    </>
  );
}

export default App;
