import React from 'react';
import { BrowserRouter, Routes, Route, Navigate } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";
import Home from "./Pages/Home";
import Search from "./Pages/Search";
import AddPost from "./Pages/AddPost";
import Chats from "./Pages/Chats";
import Profile from "./Pages/Profile";
import Login from "./Pages/Login";
import SignUp from "./Pages/SignUp";
import { login, logout } from "./authReducer"; // Импортируем экшены
import { ToastContainer, toast } from 'react-toastify';

function App() {
  const user = useSelector((state) => state.auth.user); // Получаем данные пользователя из Redux
  const dispatch = useDispatch();

  function handleLogin(userData) {
    dispatch(login(userData)); // Логин через Redux
  }

  function handleLogout() {
    dispatch(logout()); // Логаут через Redux
  }

  return (
    <>
      <ToastContainer />
      <BrowserRouter>
        <Routes>
          <Route
            path="/home" 
            element={user && user.role === null ? <Navigate to="/profile" /> : <Home />}
          />
          <Route
            path="/search"
            element={user && (user.role === null || user.role !== "moderator") ? <Navigate to="/profile" /> : <Search />}
          />
          <Route
            path="/add-post"
            element={user && user.role === null ? <Navigate to="/profile" /> : user ? <AddPost /> : <Navigate to="/profile" />}
          />
          <Route
            path="/chats"
            element={user && (user.role === null || user.role !== "admin") ? <Navigate to="/profile" /> : <Chats />}
          />
          <Route
            path="/profile"
            element={<Profile />}
          />
          <Route
            path="/login"
            element={<Login onLogin={handleLogin} />} // Передаём функцию для логина
          />
          <Route
            path="/signup"
            element={<SignUp />}
          />
        </Routes>
      </BrowserRouter>
    </>
  );
}

export default App;
