// LoginForm.jsx
import { useState } from "react";
import { useNavigate, Link } from "react-router-dom";
import { useDispatch } from "react-redux";
import { setUser } from "../store/AuthSlice";
import styles from "../Styles/LoginForm.module.css";

export default function LoginForm() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState(null);
  const navigate = useNavigate();
  const dispatch = useDispatch();

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!email || !password) {
      setError("Please fill in all fields.");
      return;
    }

    try {
      const response = await fetch("http://localhost:8080/api/auth/login", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ email, password }),
      });

      if (!response.ok) {
        throw new Error("Invalid credentials");
      }

      const userData = await response.json();
      dispatch(setUser(userData));

      if (userData.role === "USER") {
        navigate("/main");
      } else if (userData.role === "ADMIN") {
        navigate("/admin");
      } else if (userData.role === "MODERATOR") {
        navigate("/moderator");
      } else {
        throw new Error("Unknown role");
      }

    } catch (error) {
      setError(error.message);
    }
  };

  return (
    <div className={styles.loginBox}>
      <div className={styles.sign}>Sign in</div>

      <Link to="/SignUp" style={{ display: 'block' }}>
        <button className={styles.uborder}>Create new account</button>
      </Link>

      <form onSubmit={handleSubmit}>
        <button className={styles.tborder} type="submit">Sign in</button>

        <input
          type="email"
          className={styles.email}
          placeholder="Enter your email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />

        <input
          type="password"
          className={styles.password}
          placeholder="Enter your password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />

        {error && <div className={styles.error}>{error}</div>}

        <button type="button" className={styles.info}>ðŸ›ˆ</button>
      </form>

      <a href="#" className={styles.forgotp}>Forgot your password?</a>

      <Link to="/SignUp" style={{ display: 'block' }}>
        <a href="#" className={styles.reg}>Don't have an account?</a>
      </Link>
    </div>
  );
}
