import { useState } from "react";
import { useSelector } from "react-redux";
import styles from "./ModalPost.module.css";

export default function ModalPost({ onClose }) {
  const [text, setText] = useState("");
  const user = useSelector((state) => state.auth.user);

  const handlePublish = async () => {
    const today = new Date();
    const formattedDate = today.toLocaleDateString("ru-RU", {
      day: "2-digit",
      month: "long",
      year: "numeric",
    });

    const postData = {
      content: text,
      authorId: user?.id,
      date: formattedDate,
      likesCount: 0,
      commentsCount: 0,
    };

    try {
      const response = await fetch("http://localhost:8080/api/posts", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(postData),
      });

      if (!response.ok) throw new Error("Ошибка при отправке поста");

      const result = await response.text(); 
      console.log("Пост отправлен:", result);

      alert("Пост успешно опубликован!"); 
      onClose(); 
    } catch (err) {
      console.error("Ошибка при публикации:", err);
      alert("Не удалось опубликовать пост.");
    }
  };

  return (
    <div className={styles.overlay}>
      <div className={styles.modal}>
        <div className={styles.header}>
          <button onClick={onClose} className={styles.cancelButton}>Отмена</button>
          <h2>Новая ветка</h2>
        </div>

        <div className={styles.body}>
          <img src="/avatar.jpg" alt="avatar" className={styles.avatar} />
          <div className={styles.inputSection}>
            <div className={styles.username}>
              {user?.name || "Аноним"} <span>› {new Date().toLocaleDateString("ru-RU")}</span>
            </div>
            <textarea
              className={styles.textarea}
              placeholder="Что нового?"
              value={text}
              onChange={(e) => setText(e.target.value)}
            />
            <div className={styles.tools}>
              <img
                src="https://cdn-icons-png.flaticon.com/512/1829/1829586.png"
                alt="img"
              />
              <img
                src="https://cdn-icons-png.flaticon.com/512/727/727269.png"
                alt="gif"
              />
              <img
                src="https://cdn-icons-png.flaticon.com/512/724/724933.png"
                alt="attach"
              />
            </div>
            <button
              className={styles.publishButton}
              disabled={!text.trim()}
              onClick={handlePublish}
            >
              Опубликовать
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}
